import React from "react";
import { NavLink } from "react-router-dom";

const Navigation = () => {
  return (
    <div className="sidebar">
      <div className="id">
        <div className="idContent">
          <img src="./media/billGates.jpg" alt="profile-pic" />
          <h3>Renaud RIEUX</h3>
        </div>
      </div>
      <nav>
        <ul>
          <NavLink
            to="/"
            className={(nav) => (nav.isActive ? "nav-active" : "")}
          >
            <li>Accueil</li>
          </NavLink>

          <NavLink
            to="/contact"
            className={(nav) => (nav.isActive ? "nav-active" : "")}
          >
            <li>Contact</li>
          </NavLink>
          <NavLink
            to="/competences"
            className={(nav) => (nav.isActive ? "nav-active" : "")}
          >
            <li>competences</li>
          </NavLink>

          <NavLink
            to="/portfolio"
            className={(nav) => (nav.isActive ? "nav-active" : "")}
          >
            <li>portfolio</li>
          </NavLink>
        </ul>
      </nav>
    </div>
  );
};

export default Navigation;
